pub fn criterion_benchmark(c: &mut Criterion) {
    c.bench_function("decode_hex_str ('hex_bytes' from stacks) <360>", |b| b.iter(|| decode_hex_str_stacks(black_box(""))));
    c.bench_function("decode_hex_str (function from crate 'hex')  <360>", |b| b.iter(|| decode_hex_str_crate(black_box(""))));

    c.bench_function("encode_to_hex_str ('to_hex' from stacks) <360>", |b| b.iter(|| encode_hex_str_stacks()));
    c.bench_function("encode_to_hex_str (function from crate 'hex')  <360>", |b| b.iter(|| encode_hex_str_crate()));
}

criterion_group!(benches, criterion_benchmark);
criterion_main!(benches);
